import React, { Component } from 'react'
import { movies } from './getMovies'
import axios from 'axios';

export default class Movies extends Component {
  constructor(){
    super();
    this.state={
        hover:'',
        parr:[1]
    }
  }

  componentDidMount(){
      console.log('mounting done')
  }

  render() {
    let movie = movies.results
    console.log(re)
    return (
      <>
      {
        movie.length === 0 ?
        <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
        </div>:
        <div>
        <h3 className='text-centre'><strong>Trending</strong></h3>
        <div className='movies-list'>
        {
            movie.map((movieObj)=>(
            <div className="card moives-card" onMouseEnter={()=>this.setState({hover:movieObj.id})} onMouseLeave={()=>this.setState({hover:''})}>
            <img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`}  alt={movieObj.title} className="card-img-top movies-img"/>
            {/* <div className="card-body"> */}
            <h5 className="card-title movies-title">{movieObj.original_title}</h5>
            {/*<p className="card-text banner-text" >{movieObj.overview}</p>*/}
            <div className='button-wrapper' style={{display:"flex",width:'100%',justifyContent:'center'}}>
            {
                this.state.hover === movieObj.id &&
                <a className="btn btn-primary movies-button">Add to Favourites</a>
            }
            </div>
            {/* </div> */}
        </div>
        ))
        }
        </div>
        <div style={{display:'flex',justifyContent:'center'}}>
        <nav aria-label="Page navigation example">
        <ul class="pagination">
        <li class="page-item"><a class="page-link" href="#">Previous</a></li>
        {
            this.state.parr.map((value)=>(
                <li class="page-item"><a class="page-link" href="#">{value}</a></li>
            ))
        }
            <li class="page-item"><a class="page-link" href="#">Next</a></li>
        </ul>
        </nav>
        </div>
        </div>
       }
      </>
    )
  }
}
