import React, { Component } from 'react'
import { movies } from './getMovies'

export default class Movies extends Component {
  render() {
    let movie = movies.results
    return (
      <>
      {
        movie.length === 0 ?
        <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
        </div>:
        <div>
        <h3 className='text-centre'><strong>Trending</strong></h3>
        <div className='movies-list'>
        {
            movie.map((movieObj)=>(
            <div className="card moives-card">
            <img src={`https://image.tmdb.org/t/p/original${movieObj.backdrop_path}`} style={{height:'40vh'}}  alt={movieObj.title} className="card-img-top banner-img"/>
            {/* <div className="card-body"> */}
            <h1 className="card-title movies-title">{movieObj.original_title}</h1>
            {/*<p className="card-text banner-text" >{movieObj.overview}</p>*/}
            <div className='button-wrapper' style={{display:"flex",width:'100%',justifyContent:'ce'}}>
            <a href="#" className="btn btn-primary">Go somewhere</a>
            </div>
            {/* </div> */}
        </div>
        ))
        }
        </div>
        </div>
       }
      </>
    )
  }
}
